import "./Navbar.scss";
import { useState } from "react";
import { motion } from "framer-motion";

const itemVariants = {
  open: {
    opacity: 1,
    y: 0,
    transition: { type: "spring", stiffness: 300, damping: 24 }
  },
  closed: { opacity: 0, y: 20, transition: { duration: 0.2 } }
};

export default function Navbar() {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <motion.div
      className="app__navbar"
      layout
      data-isOpen={isOpen}
      transition={{ delayChildren: 1}}
      >
          <motion.nav
            initial={false}
            animate={isOpen ? "open" : "closed"}
            className="menu"
          >
            <motion.button
              whileTap={{ scale: 0.95 }}
              onClick={() => setIsOpen(!isOpen)}
              // className='tracking-in-expand'
            >
              ANDRAS BENEI
              <motion.div
                variants={{
                  open: { rotate: 180 },
                  closed: { rotate: 0 }
                }}
                transition={{ duration: 1 }}
                style={{ originY: 0.55 }}
              >
                <svg width="10" height="10" viewBox="0 0 20 20" id="svg">
                  <path d="M0 7 L 20 7 L 10 16" />
                </svg>
              </motion.div>
            </motion.button>
            <motion.ul
              variants={{
                open: {
                  clipPath: "inset(0% 0% 0% 0% round 10px)",
                  transition: {
                    type: "spring",
                    bounce: 0,
                    duration: 0.7,
                    delayChildren: 0.3,
                    staggerChildren: 0.05
                  }
                },
                closed: {
                  clipPath: "inset(10% 50% 90% 50% round 10px)",
                  transition: {
                    type: "spring",
                    bounce: 0,
                    duration: 0.3
                  }
                }
              }}
              style={{ pointerEvents: isOpen ? "auto" : "none" }}
            >
              <motion.li variants={itemVariants}>Header </motion.li>
              <motion.li variants={itemVariants}>Work </motion.li>
              <motion.li variants={itemVariants}>About </motion.li>
              <motion.li variants={itemVariants}>Contact </motion.li>
            </motion.ul>
          </motion.nav>
    </motion.div>

  );
}
